mapper.select:
SELECT * 
FROM ATTACHMENT
WHERE REF_TABLE=#{refTable}
AND REF_ID=#{refId}
AND A_RENAME NOT LIKE '%pdf' AND A_RENAME NOT LIKE '%PDF'
AND STATUS='Y'

SELECT * 
FROM ATTACHMENT
WHERE REF_TABLE=#{refTable}
AND REF_ID=#{refId}
AND A_RENAME LIKE '%pdf' OR A_RENAME LIKE '%PDF'
AND STATUS='Y'

SELECT COUNT(*)
FROM ITEM_VW
WHERE IID = #{i_id}
AND I_DIV = #{i_div}
AND STATUS='Y'

mapper.update:
UPDATE MEDICINE
SET INSURANCE=#{insurance}, M_CONTROL=#{m_control},ADD_M=#{add_m}
WHERE INO=#{i_no}

UPDATE ATTACHMENT
SET STATUS='N'
WHERE REF_TABLE=#{refTable} AND REF_ID=#{refId}

UPDATE ATTACHMENT 
SET A_ORIGINNAME=#{originalFile}, A_RENAME=#{renameFile} , STATUS=DEFAULT
WHERE REF_TABLE=#{refTable} AND REF_ID=#{refId}
